import { useState } from "react";

const prices = ({ bpi }) => {
  const [state, setState] = useState({
    currency: "USD"
  });
  const onChange = e => {
    setState({
      ...state,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div>
      <ul className="listgroup">
        {bpi.map((each, i) => {
          console.log(each, i);
        })}
      </ul>
    </div>
  );
};

export default prices;
