import { useEffect, useState } from "react";
import Axios from "axios";
import Link from "next/link";

const prices = () => {
  const [state, setState] = useState({
    res: []
  });
  useEffect(() => {
    fetchData();
  }, []);
  const fetchData = async () => {
    try {
      const data = await Axios("https://jsonplaceholder.typicode.com/users");
      setState({
        ...state,
        res: data.data
      });
    } catch (error) {
      alert(error);
    }
  };
  const deleteItem = async item => {
    try {
      const data = await Axios.delete(
        `https://jsonplaceholder.typicode.com/users/${item}`
      );
      if (data.status === 200) {
        const newResult = state.res.filter(res => {
          return res.id !== item;
        });
        setState({
          ...state,
          res: newResult
        });
        return;
      }
      return alert("Unable to delete");
    } catch (error) {
      alert(error);
    }
  };
  return (
    <div className="card">
      {state.res.map(each => {
        return (
          <div
            className="card-header d-flex justify-content-between my-2"
            key={each.id}
          >
            <h4>{each.name}</h4>
            <div>
              <button
                className="btn btn-danger mr-2"
                onClick={() => deleteItem(each.id)}
              >
                {" "}
                X{" "}
              </button>
              <Link href="/p/[id]" as={`/p/${each.id}`}>
                <a>
                  <button className="btn btn-secondary">View</button>
                </a>
              </Link>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default prices;
